import React from 'react';
import { useParams } from 'react-router-dom';
import mockMovies from '../data/mockMovies.js';
import './stylesSelectionInfoPage.css';

function SelectionInfoPage({ movies }) {
    const { id } = useParams();
    const movie = mockMovies.find(movie => movie.id === parseInt(id));

    return (

        <div className="movie-detail">
            {movie ? (
                <div>
                    <img src={movie.poster} className="movie-poster-hor" />
                    <h1>{movie.title}</h1>
                    <p>Cast: {movie.cast}</p>
                    <p>Director: {movie.director}</p>
                    <p>Duration: {movie.duration}</p>
                    <p>Genre: {movie.genre}</p>
                    <p>Release Date: {movie.releaseDate}</p>
                    <p>Distributor: {movie.distributer}</p>
                </div>
            ) : (
                <p>Movie not found</p>
            )}
        </div>
    );
}

export default SelectionInfoPage;
