// src/components/HomePage.js
import React, {useEffect} from 'react';
import { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './styles.css'; // Si quieres añadir estilos específicos
import MoviesList from './MoviesList';
import axios from "axios";

const HomePage = () => {
    const navigate = useNavigate();
    const [view, setView] = useState('billboard'); // Change from bildoard to snack menu

    const [movies, setMovies] = useState([]);

    const handleLogin = () => {
        navigate('/login'); // Goes to login page
    };

    const handleViewChange = (newView) => {
        setView(newView); // Cambia la vista entre Cartelera y Snacks
    };


    useEffect(() => {
        // Fetch the list of movies currently in theaters from the API
        axios.get('/api/movies/currently-available')
            .then(response => setMovies(response.data))
            .catch(error => console.error('Error fetching movies!', error));
    }, []);

    return (
        <div className="home-page">
            {/* Top Bar */}
            <div className="navbar">
                <h1 className="cine-name">What The Fun Cinema</h1>
                <button className="nav-button" onClick={() => handleViewChange('billboard')}>Billboard</button>
                <button className="nav-button" onClick={() => handleViewChange('snacks')}>Snacks</button>

                {/* Placeholder for filters*/}

                <button className="login-button" onClick={handleLogin}>
                    Login
                </button>
            </div>

            {/* Principal content */}
            <div className="content">
                {view === 'billboard' ? (
                    <div>
                        <h2>FILMS AVAILABLE</h2>
                        <MoviesList />
                    </div>
                ) : (
                    <div>
                        <h2>Snacks</h2>
                        <p>Snacks page</p>
                    </div>
                )}
            </div>
        </div>
    );
};

export default HomePage;
